document.addEventListener("DOMContentLoaded",function(){if(!document.body.classList.contains("mobile")){return}const notifContainer=document.createElement("div");notifContainer.classList.add("notif-wd-container");document.body.appendChild(notifContainer);function generateUsername(){const hurufKonsonan="BCDFGHJKLMNPQRSTVWXYZ";const hurufVokal="AIUEO";const huruf1=hurufKonsonan[Math.floor(Math.random()*hurufKonsonan.length)];const huruf2=hurufVokal[Math.floor(Math.random()*hurufVokal.length)];const huruf3=hurufKonsonan[Math.floor(Math.random()*hurufKonsonan.length)];return huruf1+huruf2+huruf3+"***"}function generateAmount(){const minAmount=1e4;const maxAmount=1e7;let amount=Math.floor(Math.random()*(maxAmount-minAmount+1))+minAmount;amount=Math.round(amount/1e3)*1e3;return new Intl.NumberFormat("id-ID").format(amount)}function showNotification(){if(document.querySelectorAll(".notif-wd.show").length>=3){return}const randomUsername=generateUsername();const randomAmount=generateAmount();const notif=document.createElement("div");notif.classList.add("notif-wd");notif.innerHTML=`
            <img src="https://files.sitestatic.net/ImageFile/202407021541480000005110aaff772GGIGAA__300x300.png" 
                 style="width: 16px; height: 16px; margin-right: 5px; vertical-align: middle;"> 
            <span>${randomUsername} berhasil WD sebesar ðŸš€ Rp ${randomAmount}</span>`;notifContainer.appendChild(notif);setTimeout(()=>{notif.classList.add("show")},100);setTimeout(()=>{notif.classList.remove("show");notif.classList.add("notif-wd-hide");notif.addEventListener("transitionend",function(event){if(event.propertyName==="opacity"&&notif.classList.contains("notif-wd-hide")){notif.remove()}})},2500)}function startNotificationLoop(){setTimeout(()=>{showNotification();startNotificationLoop()},Math.floor(Math.random()*(6e3-2500+1))+2500)}requestAnimationFrame(()=>{showNotification()});startNotificationLoop()});
