document.addEventListener("DOMContentLoaded",function(){if(!document.body.classList.contains("mobile")){return}const notifContainer=document.createElement("div");notifContainer.classList.add("notif-wd-container");document.body.appendChild(notifContainer);const usernames=["Mega","Uco","Ahm","Jac","Sup","Lis","Ima","Bud","Ilh","Bur","Gul","Zak","Moh","Muh","Zeu","Saf","Rud","Eko","Ring","Nin","Ind","Kar","Sus","Sam","Elo","Eli","Vic","Fitr","Dom","Immo","Rul","Ikr","Shad","Cin","Mel","Sya","Eva","Reb","Fen","Ven","Des","Iro","Rok","Arm","Hen","Hal","Lus","Wis","Fir","Mas","Tan","Pro","Ber","Dea","Sku","Lau","Ver","Sep","Lia","Dar","Nan","Gho","Sap","Sud","Pit","Ram","Pen","Cha","Hel","Mar","Pha","Cel","Emi","Inf","Nex","Wah","Sta","Fro","Wra","Sak","Pop","Ism","Sut","Luf","Nur","Kev","Fer","Tog","Jan","Nov","Nev","Mon","Sit","Ste","Cos","Bla","Dar","Net","Udi","Abi","War","Gal","Jef","Zep","Sha","Ben","Sol","Fur","Voi","Par","Ris","Uja","Bus","Agu","Lun","Mhd","Mer","Riz"];function generateUsername(){return usernames[Math.floor(Math.random()*usernames.length)]+"***"}function generateAmount(){const minAmount=1e5;const maxAmount=1e7;let amount=Math.floor(Math.random()*(maxAmount-minAmount+1))+minAmount;if(amount>=1e6){amount=Math.round(amount/1e5)*1e5}else{amount=Math.round(amount/5e4)*5e4}return new Intl.NumberFormat("id-ID").format(amount)}function showNotification(){if(document.querySelectorAll(".notif-wd.show").length>=3){return}const randomUsername=generateUsername();const randomAmount=generateAmount();const notif=document.createElement("div");notif.classList.add("notif-wd");notif.innerHTML=`<img src="https://files.sitestatic.net/ImageFile/202407021541480000005110aaff772GGIGAA__300x300.png"style="width: 16px; height: 16px; margin-right: 5px; vertical-align: middle;"><span>${randomUsername} berhasil WD sebesar ðŸš€ Rp ${randomAmount}</span>`;notifContainer.appendChild(notif);setTimeout(()=>{notif.classList.add("show")},100);setTimeout(()=>{notif.classList.remove("show");notif.classList.add("notif-wd-hide");notif.addEventListener("transitionend",function(event){if(event.propertyName==="opacity"&&notif.classList.contains("notif-wd-hide")){notif.remove()}})},2500)}function startNotificationLoop(){setTimeout(()=>{showNotification();startNotificationLoop()},Math.floor(Math.random()*(6e3-2500+1))+2500)}requestAnimationFrame(()=>{showNotification()});startNotificationLoop()});